            //            d = ['M', this.sourceCoords.x, this.sourceCoords.y, 'C', controlX, controlY, controlX, controlY, this.targetCoords.x, this.targetCoords.y].join(' ');

            d = ['M', pp.sx, pp.sy, controlPath, pp.tx, pp.ty].join(' ');
